package com.yanfei1819.hystrixdashboard.controller;import com.netflix.hystrix.contrib.javanica.annotation.HystrixCommand;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;/** * Created by 追梦1819 on 2019-03-27. */@RestControllerpublic class DashboardController {    @Value("${server.port}")    String port;    @RequestMapping("/hi")    @HystrixCommand(fallbackMethod = "hiError")    public String home(@RequestParam(value = "name", defaultValue = "forezp") String name) {        return "hi " + name + " ,i am from port:" + port;    }    public String hiError(String name) {        return "hi,"+name+",sorry,error!";    }}