package com.yanfei1819.zipkina.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.client.RestTemplate;/** * 对外提供的测试接口 * * Created by 追梦1819 on 2019-03-28. */@RestControllerpublic class ZipkinAController {    @Autowired    private RestTemplate restTemplate;    /**     * 调用 zipkin-b     */    @GetMapping("/zipkinAHello")    public String zipkinAHello(){        return restTemplate.getForObject("http://zipkin-b/zipkinBProvider",String.class);    }    /**     * 对外（zipkin-a）提供的接口     */    @GetMapping("/zipkinAProvider")    public String zipkinProviderHello(){        return "hello,我被zipkin-a调用，测试zipkin";    }}