package com.yanfei1819.zipkinb.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.client.RestTemplate;/** * 对外提供的测试接口 * * Created by 追梦1819 on 2019-03-28. */@RestControllerpublic class ZipkinBController {    @Autowired    private RestTemplate restTemplate;    /**     * 调用 zipkin-a     */    @GetMapping("/zipkinBHello")    public String zipkinBHello(){        return restTemplate.getForObject("http://zipkin-a/zipkinAProvider",String.class);    }    /**     * 对外（zipkin-a）提供的接口     */    @GetMapping("/zipkinBProvider")    public String zipkinBProvider(){        return "hello,我被zipkin-a调用，测试zipkin";    }}